import React from 'react';

const Card = (p) => {
    const {name, email, id} = props;
    return (
        <div className='tc bg-light-green dib br3 pa3 ma2 grow p2 shadow-5'>
            <img src={`https://robohash.org/${id}?200x200`} alt='photo'/>
            <div>
                <h2>{name}</h2>
                <p>{email}</p>
            </div>
        </div>

    );
}

export default Card;
